        Body = "{\"blocks\":[{\"key\":\"bn401\",\"text\":\"\u0686\u062A \u062C\u06CC \u067E\u06CC \u062A\u06CC (ChatGPT) \u062C\u062F\u06CC\u062F\u062A\u0631\u06CC\u0646 \u00AB\u0645\u062F\u0644 \u0632\u0628\u0627\u0646\u06CC\u00BB (Language Model) \u0645\u0628\u062A\u0646\u06CC \u0628\u0631 \u0647\u0648\u0634 \u0645\u0635\u0646\u0648\u0639\u06CC \u0627\u0631\u0627\u0626\u0647 \u0634\u062F\u0647 \u062A\u0648\u0633\u0637 \u0645\u062C\u0645\u0648\u0639\u0647 OpenAI \u0628\u0647 \u062D\u0633\u0627\u0628 \u0645\u06CC\u200C\u0622\u06CC\u062F. \u0627\u06CC\u0646 \u0645\u0637\u0644\u0628\u060C \u0631\u0627\u0647\u0646\u0645\u0627\u06CC\u06CC \u062C\u0627\u0645\u0639 \u0628\u0631\u0627\u06CC \u0646\u062D\u0648\u0647 \u0627\u0633\u062A\u0641\u0627\u062F\u0647 \u0627\u0632 \u062F\u0633\u062A\u06CC\u0627\u0631 \u0645\u062C\u0627\u0632\u06CC Chat GPT \u0627\u0633\u062A. \u062F\u0631 \u0627\u06CC\u0646 \u0645\u0637\u0644\u0628 \u0627\u0628\u062A\u062F\u0627 \u0628\u0647 \u0627\u06CC\u0646 \u067E\u0631\u0633\u0634 \u067E\u0627\u0633\u062E \u0645\u06CC\u200C\u062F\u0647\u06CC\u0645 \u06A9\u0647 ChatGPT \u0686\u06CC\u0633\u062A \u0648 \u0633\u067E\u0633 \u067E\u06CC\u0631\u0627\u0645\u0648\u0646 \u0646\u062D\u0648\u0647 \u062F\u0633\u062A\u0631\u0633\u06CC \u0628\u0647 ChatGPT\u060C \u0686\u06AF\u0648\u0646\u06AF\u06CC \u0634\u0631\u0648\u0639 \u0628\u0647 \u06A9\u0627\u0631 \u0628\u0627 \u0622\u0646\u060C \u06A9\u0627\u0631\u0628\u0631\u062F\u0647\u0627\u06CC \u0627\u0635\u0644\u06CC \u0648 \u0645\u0647\u0645 \u0686\u062A \u062C\u06CC \u067E\u06CC \u062A\u06CC \u0648 \u0641\u0648\u0627\u06CC\u062F \u0622\u0646 \u062E\u0648\u0627\u0647\u06CC\u062F \u0622\u0645\u0648\u062E\u062A. \u0634\u0645\u0627 \u0628\u0627 \u0645\u0637\u0627\u0644\u0639\u0647 \u0627\u06CC\u0646 \u0645\u0637\u0644\u0628 \u06CC\u0627\u062F \u062E\u0648\u0627\u0647\u06CC\u062F \u06AF\u0631\u0641\u062A \u06A9\u0647 \u0686\u0637\u0648\u0631 \u0627\u0632 ChatGPT \u0628\u0647 \u0646\u0641\u0639 \u062E\u0648\u062F \u06